void exe(instr i, state *s)
{
    switch (i.opcode)
    {
    case 0:
        // mov reg[op1], reg[op2]
        s->reg[i.op1] = s->reg[i.op2];
        break;
    case 1:
        // movi reg[op2], $imm
        s->reg[i.op1] = i.op2;
        break;
    case 2:
        // addi reg[op1], $imm
        s->reg[i.op1] += i.op2;
        break;
    case 3:
        // muli reg[i.op1], $imm
        s->reg[i.op1] *= i.op2;
        break;
    case 4:
        // add reg[i.op1], reg[i.op2]
        s->reg[i.op1] += s->reg[i.op2];
        break;
    case 5:
        // mul reg[i.op1], reg[i.op2]
        s->reg[i.op1] *= s->reg[i.op2];
        break;
    case 6:
        // mmovf reg[i.op1], mem[i.op2]
        s->reg[i.op1] = s->memory[i.op2];
        break;
    case 7:
        // mstore reg[i.op1], mem[i.op2]
        s->memory[i.op2] = s->reg[i.op1];
        break;
    case 8:
        // jz .label
        if (s->zf)
            s->ip += 3 * ((i.op1 << 8) | i.op2);
        break;
    case 9:
        // jnz .label
        if (!(s->zf))
            s->ip += 3 * ((i.op1 << 8) | i.op2);
        break;
    case 10:
        // cmp reg[i.op1], reg[i.op2]
        s->zf = s->reg[i.op1] == s->reg[i.op2];
        break;
    case 11:
        // cmpi reg[i.op1], $imm
        s->zf = s->reg[i.op1] == i.op2;
        break;
    case 12:
        // xor reg[i.op1], reg[i.op2]
        s->reg[i.op1] ^= s->reg[i.op2];
        break;
    case 13:
        // and reg[i.op1], reg[i.op2]
        s->reg[i.op1] &= s->reg[i.op2];
        break;
    case 14:
        // or reg[i.op1], reg[i.op2]
        s->reg[i.op1] |= s->reg[i.op2];
        break;
    case 15:
        // jmp .label
        s->ip += 3 * ((i.op1 << 8) | i.op2);
        break;
    case 16:
        // halt
        s->halt = 1;
        break;
    default:
        break;
    }
}

instr decode(unsigned char *bytecode, int *ip)
{
    instr i;
    i.opcode = bytecode[(*ip)++];
    i.op1 = bytecode[(*ip)++];
    i.op2 = bytecode[(*ip)++];
    return i;
}

int check(char *input)
{
    state s;
    memset(&s, 0, sizeof(state));
    *(int *)s.memory = strlen(input);
    memcpy(s.memory + 16, input, strlen(input));
    instr i = decode(bytecode, &s.ip);
    while (!s.halt)
    {
        exe(i, &s);
        i = decode(bytecode, &s.ip);
    }
    return s.reg[0];
}
int main(int argc, char **argv)
{
    if (argc < 2)
    {
        printf("Usage: %s <flag>\n", argv[0]);
        return 1;
    }
    char *input = argv[1];
    if (strlen(input) > 1000)
    {
        printf("Flag too long\n");
        return 1;
    }
    if (check(input))
    {
        printf("Correct flag\n");
        return 0;
    }
    else
    {
        printf("Incorrect flag\n");
        return 1;
    }
}